mapper.select:
select

from t_acct_diary
where account_book_id = #{accountBookId,jdbcType=VARCHAR}
and acctno = #{acctno,jdbcType=VARCHAR}
and periodday = #{periodday,jdbcType=VARCHAR}

mapper.delete:
delete from t_acct_diary
where account_book_id = #{accountBookId,jdbcType=VARCHAR}
and acctno = #{acctno,jdbcType=VARCHAR}
and periodday = #{periodday,jdbcType=VARCHAR}
