mapper.delete:
delete from t_acct_entry_detail
where voucherno = #{voucherno,jdbcType=VARCHAR}
  and entryno = #{entryno,jdbcType=BIGINT}
  and account_book_id = #{accountBookId,jdbcType=VARCHAR}
  and entrypty = #{entrypty,jdbcType=BIGINT}

mapper.select:
select voucherno, entryno, account_book_id, entrypty, vouchertype, iscusacct, acctno, 
currtype, pid, trxcode, trxid, refvoucherno, parenttrxid, childtrxid, entrydate, 
entrytime, crdr, amount, curbal, reciporgtype, reciporgcode, recipacctno, postflag, 
postdate, posttime, postbatch, genledflag, genleddate, genledtime, genledbatch, memo, 
isrevd, entrytype, revtime, revtype, entrybatch, acctname, reglimitcus, reglimitca, 
reglimitacct, accttype, id
from t_acct_entry_detail
where voucherno = #{voucherno,jdbcType=VARCHAR}
  and entryno = #{entryno,jdbcType=BIGINT}
  and account_book_id = #{accountBookId,jdbcType=VARCHAR}
  and entrypty = #{entrypty,jdbcType=BIGINT}
