mapper.delete:
delete from t_acct_genled
where account_book_id = #{accountBookId,jdbcType=VARCHAR}
  and itemcode = #{itemcode,jdbcType=VARCHAR}

mapper.select:
select account_book_id, itemcode, itembalby, itemclass, currtype, lydrbal, lycrbal, 
tydramt, tycramt, tydrcnt, tycrcnt, lmdrbal, lmcrbal, tmdramt, tmcramt, tmdrcnt, 
tmcrcnt, lddrbal, ldcrbal, tddramt, tdcramt, tddrcnt, tdcrcnt, tddrbal, tdcrbal
from t_acct_genled
where account_book_id = #{accountBookId,jdbcType=VARCHAR}
  and itemcode = #{itemcode,jdbcType=VARCHAR}
